import React, { useState, useEffect } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { useSearchParams } from 'react-router-dom';
import './MediaContent.css';

const MediaContent = () => {
  const { t } = useLanguage();
  const [searchParams] = useSearchParams();
  const [mediaContent, setMediaContent] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredMedia, setFilteredMedia] = useState([]);

  useEffect(() => {
    // TODO: Fetch media content from API
    setMediaContent([
      {
        id: 1,
        title: 'Breaking News Coverage',
        description: 'Exclusive coverage of major international event',
        mediaType: 'Photo',
        price: 500,
        licenseType: 'Rights Managed',
        journalist: 'John Doe',
        rating: 4.5,
        views: 45
      },
      {
        id: 2,
        title: 'Event Photography',
        description: 'Professional event coverage',
        mediaType: 'Photo',
        price: 300,
        licenseType: 'Royalty Free',
        journalist: 'Jane Smith',
        rating: 4.2,
        views: 23
      },
      {
        id: 3,
        title: 'Syria Conflict Documentary',
        description: 'In-depth documentary covering the ongoing conflict in Syria',
        mediaType: 'Video',
        price: 800,
        licenseType: 'Rights Managed',
        journalist: 'Sarah Johnson',
        rating: 4.8,
        views: 67
      }
    ]);
    setLoading(false);
  }, []);

  useEffect(() => {
    if (mediaContent.length > 0) {
      let filtered = [...mediaContent];
      
      // Filter by specific media if specified
      const mediaId = searchParams.get('mediaId');
      if (mediaId) {
        filtered = filtered.filter(media => media.id === parseInt(mediaId));
      }
      
      setFilteredMedia(filtered);
    }
  }, [mediaContent, searchParams]);

  if (loading) {
    return <div className="loading">{t('common.loading')}</div>;
  }

  return (
    <div className="media-content-container">
      <div className="media-content-header">
        <h1>{t('media.title')}</h1>
        <p>{t('media.subtitle')}</p>
      </div>

      <div className="media-content-grid">
        {filteredMedia.length > 0 ? (
          filteredMedia.map(item => (
            <div key={item.id} className="media-card">
              <div className="media-preview">
                <div className="media-placeholder">
                  {item.mediaType === 'Photo' ? '📷' : '🎥'}
                </div>
              </div>
              <div className="media-info">
                <h3>{item.title}</h3>
                <p className="media-description">{item.description}</p>
                <div className="media-meta">
                  <span className="media-type">{item.mediaType}</span>
                  <span className="media-license">{item.licenseType}</span>
                </div>
                <div className="media-author">{t('media.by')} {item.journalist}</div>
                <div className="media-stats">
                  <span>★ {item.rating}</span>
                  <span>👁 {item.views} {t('media.views')}</span>
                </div>
                <div className="media-price">${item.price}</div>
                <button className="purchase-button">{t('media.purchase')}</button>
              </div>
            </div>
          ))
        ) : (
          <div className="no-media">
            <p>{t('media.noMediaFound')}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default MediaContent;
