import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useLanguage } from '../../contexts/LanguageContext';
import '../Profile.css';

const JournalistProfile = () => {
  const { id } = useParams();
  const { t } = useLanguage();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        setLoading(true);
        setError(null);
        
        const response = await fetch(`http://localhost:3001/journalists/${id}`);
        
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        
        const data = await response.json();
        setProfile(data);
      } catch (error) {
        console.error('Error fetching journalist profile:', error);
        setError('Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    if (id) {
      fetchProfile();
    }
  }, [id]);

  if (loading) {
    return <div className="loading">{t('common.loading')}</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!profile) {
    return <div className="error">{t('common.profileNotFound')}</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-avatar">
          <img src={profile.user?.profilePicture || '/default-avatar.png'} alt="Profile" />
        </div>
        <div className="profile-info">
          <h1>{profile.user?.firstName} {profile.user?.lastName}</h1>
          <p className="profile-location">{profile.user?.city}, {profile.user?.country}</p>
          <p className="profile-specialty">{profile.mediaWorkType}</p>
          <div className="profile-rating">
            <span className="rating">â˜… {profile.rating || 0}</span>
            <span className="projects">{profile.completedProjects || 0} {t('journalists.projects')}</span>
          </div>
        </div>
      </div>

      <div className="profile-content">
        <div className="profile-section">
          <h2>{t('journalists.bio')}</h2>
          <p>{profile.bio}</p>
        </div>

        <div className="profile-section">
          <h2>{t('journalists.skills')}</h2>
          <div className="skills-grid">
            {profile.skills && JSON.parse(profile.skills).map((skill, index) => (
              <span key={index} className="skill-tag">{skill}</span>
            ))}
          </div>
        </div>

        <div className="profile-section">
          <h2>{t('journalists.languages')}</h2>
          <div className="languages-grid">
            {profile.languages && JSON.parse(profile.languages).map((language, index) => (
              <span key={index} className="language-tag">{language}</span>
            ))}
          </div>
        </div>

        {profile.certifications && JSON.parse(profile.certifications).length > 0 && (
          <div className="profile-section">
            <h2>{t('journalists.certifications')}</h2>
            <ul className="certifications-list">
              {JSON.parse(profile.certifications).map((certification, index) => (
                <li key={index}>{certification}</li>
              ))}
            </ul>
          </div>
        )}

        <div className="profile-section">
          <h2>{t('journalists.equipment')}</h2>
          <div className="equipment-info">
            <p><strong>{t('journalists.camera')}:</strong> {profile.cameraType || t('journalists.notSpecified')}</p>
            <p><strong>{t('journalists.audioEquipment')}:</strong> {profile.audioEquipmentType || t('journalists.notSpecified')}</p>
          </div>
        </div>

        <div className="profile-section">
          <h2>{t('journalists.rates')}</h2>
          <div className="rates-info">
            <p><strong>{t('journalists.hourlyRate')}:</strong> ${profile.hourlyRate || 0}/hour</p>
            <p><strong>{t('journalists.dailyRate')}:</strong> ${profile.dailyRate || 0}/day</p>
            <p><strong>{t('journalists.projectRate')}:</strong> ${profile.projectRate || 0}/project</p>
          </div>
        </div>

        <div className="profile-section">
          <h2>{t('journalists.contact')}</h2>
          <div className="contact-info">
            <p><strong>{t('common.email')}:</strong> {profile.user?.email}</p>
            <p><strong>{t('common.phone')}:</strong> {profile.user?.phone}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JournalistProfile;
