import React, { useState } from 'react';
import './Search.css';

const Search = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchType, setSearchType] = useState('all');
  const [results, setResults] = useState([]);

  const handleSearch = (e) => {
    e.preventDefault();
    // TODO: Implement search API call
    console.log('Searching for:', searchTerm, 'Type:', searchType);
  };

  return (
    <div className="search-container">
      <div className="search-header">
        <h1>Search WeSourceYou</h1>
        <p>Find journalists, companies, jobs, and media content</p>
      </div>

      <form onSubmit={handleSearch} className="search-form">
        <div className="search-input-group">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Search for journalists, companies, jobs, or media content..."
            className="search-input"
          />
          <select
            value={searchType}
            onChange={(e) => setSearchType(e.target.value)}
            className="search-type-select"
          >
            <option value="all">All</option>
            <option value="journalists">Journalists</option>
            <option value="companies">Companies</option>
            <option value="jobs">Jobs</option>
            <option value="media">Media Content</option>
          </select>
          <button type="submit" className="search-button">
            Search
          </button>
        </div>
      </form>

      <div className="search-results">
        <p>Search functionality will be implemented here.</p>
      </div>
    </div>
  );
};

export default Search;
