import React, { useState, useEffect } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { useNavigate } from 'react-router-dom';
import { FiHome, FiMapPin, FiUsers, FiGlobe, FiStar } from 'react-icons/fi';
import './Companies.css';

const Companies = () => {
  const { t } = useLanguage();
  const navigate = useNavigate();
  const [companies, setCompanies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // TODO: Fetch companies from API
    setCompanies([
      {
        id: 1,
        name: 'Global Media Network',
        description: 'Leading international media company specializing in news coverage and documentary production.',
        industry: 'Media & Entertainment',
        location: 'New York, NY',
        employeeCount: '500-1000',
        website: 'www.globalmedianetwork.com',
        rating: 4.5,
        verified: true,
        activeJobs: 12,
        founded: '2010'
      },
      {
        id: 2,
        name: 'Digital News Corp',
        description: 'Innovative digital media company focused on technology and business news.',
        industry: 'Digital Media',
        location: 'San Francisco, CA',
        employeeCount: '100-500',
        website: 'www.digitalnewscorp.com',
        rating: 4.2,
        verified: true,
        activeJobs: 8,
        founded: '2015'
      },
      {
        id: 3,
        name: 'Regional Broadcasting',
        description: 'Local news and entertainment broadcasting company serving regional communities.',
        industry: 'Broadcasting',
        location: 'Chicago, IL',
        employeeCount: '50-100',
        website: 'www.regionalbroadcasting.com',
        rating: 4.0,
        verified: false,
        activeJobs: 5,
        founded: '2008'
      }
    ]);
    setLoading(false);
  }, []);

  const handleViewJobs = (companyId, companyName) => {
    navigate(`/jobs?company=${companyId}&companyName=${encodeURIComponent(companyName)}`);
  };

  if (loading) {
    return <div className="loading">{t('common.loading')}</div>;
  }

  return (
    <div className="companies-container">
      <div className="companies-header">
        <h1>{t('companies.title')}</h1>
        <p>{t('companies.subtitle')}</p>
      </div>

      <div className="companies-list">
        {companies.map(company => (
          <div key={company.id} className="company-card">
                            <div className="company-header">
                  <div className="company-logo">
                    <FiHome className="company-icon" />
                  </div>
              <div className="company-info">
                <div className="company-name-section">
                  <h3>{company.name}</h3>
                  {company.verified && (
                    <span className="verified-badge">
                      <FiStar /> {t('companies.verified')}
                    </span>
                  )}
                </div>
                <div className="company-rating">
                  <FiStar className="star-icon" />
                  <span>{company.rating}</span>
                </div>
              </div>
            </div>

            <p className="company-description">{company.description}</p>

            <div className="company-details">
              <div className="detail-item">
                <FiMapPin className="detail-icon" />
                <span>{company.location}</span>
              </div>
              <div className="detail-item">
                <FiUsers className="detail-icon" />
                <span>{company.employeeCount} {t('companies.employees')}</span>
              </div>
              <div className="detail-item">
                <FiGlobe className="detail-icon" />
                <a href={`https://${company.website}`} target="_blank" rel="noopener noreferrer">
                  {company.website}
                </a>
              </div>
            </div>

            <div className="company-meta">
              <span className="company-industry">{company.industry}</span>
              <span className="company-founded">{t('companies.founded')}: {company.founded}</span>
            </div>

            <div className="company-footer">
              <span className="active-jobs">
                {company.activeJobs} {t('companies.activeJobs')}
              </span>
              <button 
                className="view-jobs-button"
                onClick={() => handleViewJobs(company.id, company.name)}
              >
                {t('companies.viewJobs')}
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Companies;
