import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import './Auth.css';

const RegisterJournalist = () => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const { register, handleSubmit, formState: { errors }, watch } = useForm();

  const mediaWorkTypes = [
    'Producer', 'Reporter', 'TV Cameraman', 'Photographer', 
    'Video Editor', 'Trainer', 'Graphic Designer', 'Lawyer', 
    'Voice-over artist', 'Translator', 'Analyst'
  ];

  const analystSpecialties = [
    'Political', 'Economic', 'Social', 'Cultural', 'Sports', 
    'Technology', 'Health', 'Environment', 'International Relations', 'Other'
  ];

  const onSubmit = async (data) => {
    setIsLoading(true);
    try {
      // TODO: Implement registration API call
      console.log('Journalist registration data:', data);
      navigate('/login');
    } catch (error) {
      console.error('Registration error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h1>Journalist Registration</h1>
          <p>Create your professional profile</p>
        </div>

        <form onSubmit={handleSubmit(onSubmit)} className="auth-form">
          <div className="form-group">
            <label>Full Name *</label>
            <input
              type="text"
              {...register('name', { required: 'Full name is required' })}
              placeholder="Enter your full name"
            />
            {errors.name && <span className="error">{errors.name.message}</span>}
          </div>

          <div className="form-group">
            <label>Email *</label>
            <input
              type="email"
              {...register('email', { 
                required: 'Email is required',
                pattern: {
                  value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                  message: 'Invalid email address'
                }
              })}
              placeholder="Enter your email"
            />
            {errors.email && <span className="error">{errors.email.message}</span>}
          </div>

          <div className="form-group">
            <label>Password *</label>
            <input
              type="password"
              {...register('password', { 
                required: 'Password is required',
                minLength: {
                  value: 8,
                  message: 'Password must be at least 8 characters'
                }
              })}
              placeholder="Create a password"
            />
            {errors.password && <span className="error">{errors.password.message}</span>}
          </div>

          <div className="form-group">
            <label>Confirm Password *</label>
            <input
              type="password"
              {...register('confirmPassword', { 
                required: 'Please confirm your password',
                validate: value => value === watch('password') || 'Passwords do not match'
              })}
              placeholder="Confirm your password"
            />
            {errors.confirmPassword && <span className="error">{errors.confirmPassword.message}</span>}
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Country *</label>
              <input
                type="text"
                {...register('country', { required: 'Country is required' })}
                placeholder="Enter your country"
              />
              {errors.country && <span className="error">{errors.country.message}</span>}
            </div>

            <div className="form-group">
              <label>City *</label>
              <input
                type="text"
                {...register('city', { required: 'City is required' })}
                placeholder="Enter your city"
              />
              {errors.city && <span className="error">{errors.city.message}</span>}
            </div>
          </div>

          <div className="form-group">
            <label>Phone Number *</label>
            <input
              type="tel"
              {...register('phoneNumber', { required: 'Phone number is required' })}
              placeholder="Enter your phone number"
            />
            {errors.phoneNumber && <span className="error">{errors.phoneNumber.message}</span>}
          </div>

          <div className="form-group">
            <label>Date of Birth *</label>
            <input
              type="date"
              {...register('dateOfBirth', { required: 'Date of birth is required' })}
            />
            {errors.dateOfBirth && <span className="error">{errors.dateOfBirth.message}</span>}
          </div>

          <div className="form-group">
            <label>Media Work Start Date *</label>
            <input
              type="date"
              {...register('mediaWorkStartDate', { required: 'Media work start date is required' })}
            />
            {errors.mediaWorkStartDate && <span className="error">{errors.mediaWorkStartDate.message}</span>}
          </div>

          <div className="form-group">
            <label>Type of Media Work *</label>
            <select {...register('mediaWorkType', { required: 'Media work type is required' })}>
              <option value="">Select your media work type</option>
              {mediaWorkTypes.map(type => (
                <option key={type} value={type}>{type}</option>
              ))}
            </select>
            {errors.mediaWorkType && <span className="error">{errors.mediaWorkType.message}</span>}
          </div>

          <div className="form-group">
            <label>Analyst Specialty (if applicable)</label>
            <select {...register('analystSpecialty')}>
              <option value="">Select specialty (optional)</option>
              {analystSpecialties.map(specialty => (
                <option key={specialty} value={specialty}>{specialty}</option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Bio</label>
            <textarea
              {...register('bio')}
              placeholder="Tell us about yourself and your experience"
              rows="4"
            />
          </div>

          <div className="form-group">
            <label>Skills (comma-separated)</label>
            <input
              type="text"
              {...register('skills')}
              placeholder="e.g., Photojournalism, Video Editing, Interviewing"
            />
          </div>

          <div className="form-group">
            <label>Languages (comma-separated)</label>
            <input
              type="text"
              {...register('languages')}
              placeholder="e.g., English, Spanish, French"
            />
          </div>

          <div className="form-group">
            <label>Previous Work Links (one per line)</label>
            <textarea
              {...register('previousWorkLinks')}
              placeholder="https://example.com/portfolio1&#10;https://example.com/portfolio2"
              rows="3"
            />
          </div>

          <div className="form-group">
            <label>Social Media Accounts</label>
            <div className="social-media-inputs">
              <input
                type="url"
                {...register('socialMedia.facebook')}
                placeholder="Facebook URL"
              />
              <input
                type="url"
                {...register('socialMedia.twitter')}
                placeholder="Twitter URL"
              />
              <input
                type="url"
                {...register('socialMedia.youtube')}
                placeholder="YouTube URL"
              />
              <input
                type="url"
                {...register('socialMedia.other')}
                placeholder="Other social media URL"
              />
            </div>
          </div>

          <div className="form-group">
            <label>Equipment Information</label>
            <div className="equipment-inputs">
              <div className="checkbox-group">
                <input
                  type="checkbox"
                  {...register('hasCamera')}
                  id="hasCamera"
                />
                <label htmlFor="hasCamera">I own a camera</label>
              </div>
              <input
                type="text"
                {...register('cameraType')}
                placeholder="Camera type (if applicable)"
              />
              
              <div className="checkbox-group">
                <input
                  type="checkbox"
                  {...register('hasAudioEquipment')}
                  id="hasAudioEquipment"
                />
                <label htmlFor="hasAudioEquipment">I own audio equipment</label>
              </div>
              <input
                type="text"
                {...register('audioEquipmentType')}
                placeholder="Audio equipment type (if applicable)"
              />
              
              <div className="checkbox-group">
                <input
                  type="checkbox"
                  {...register('canTravel')}
                  id="canTravel"
                />
                <label htmlFor="canTravel">I can travel for work</label>
              </div>
            </div>
          </div>

          <button type="submit" className="auth-button" disabled={isLoading}>
            {isLoading ? 'Creating Account...' : 'Create Account'}
          </button>
        </form>

        <div className="auth-footer">
          <p>
            Already have an account? <Link to="/login">Sign In</Link>
          </p>
          <p>
            <Link to="/register">‚Üê Back to registration options</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default RegisterJournalist;
