import React, { useState, useEffect } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { useNavigate } from 'react-router-dom';
import { FiUser, FiMapPin, FiCamera, FiStar, FiAward, FiGlobe } from 'react-icons/fi';
import './Journalists.css';

const Journalists = () => {
  const { t } = useLanguage();
  const navigate = useNavigate();
  const [journalists, setJournalists] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // TODO: Fetch journalists from API
    setJournalists([
      {
        id: 1,
        name: 'Sarah Johnson',
        bio: 'Award-winning photojournalist with 15 years of experience covering international conflicts and humanitarian crises.',
        specialty: 'Photojournalism',
        location: 'London, UK',
        experience: '15 years',
        rating: 4.8,
        verified: true,
        mediaCount: 45,
        languages: ['English', 'French', 'Arabic'],
        equipment: ['Professional Camera', 'Drone', 'Satellite Phone'],
        awards: ['Pulitzer Prize Finalist', 'World Press Photo Winner']
      },
      {
        id: 2,
        name: 'Ahmed Hassan',
        bio: 'Investigative journalist specializing in Middle Eastern politics and social issues. Fluent in Arabic and English.',
        specialty: 'Investigative Journalism',
        location: 'Cairo, Egypt',
        experience: '8 years',
        rating: 4.6,
        verified: true,
        mediaCount: 32,
        languages: ['Arabic', 'English'],
        equipment: ['Video Camera', 'Audio Recorder'],
        awards: ['Arab Journalism Award']
      },
      {
        id: 3,
        name: 'Maria Rodriguez',
        bio: 'Digital media specialist focusing on technology and business news. Expert in multimedia storytelling.',
        specialty: 'Digital Media',
        location: 'Barcelona, Spain',
        experience: '6 years',
        rating: 4.4,
        verified: false,
        mediaCount: 28,
        languages: ['Spanish', 'English', 'Catalan'],
        equipment: ['4K Camera', 'Editing Suite'],
        awards: []
      }
    ]);
    setLoading(false);
  }, []);

  const handleViewProfile = (journalistId) => {
    navigate(`/journalist/${journalistId}`);
  };

  if (loading) {
    return <div className="loading">{t('common.loading')}</div>;
  }

  return (
    <div className="journalists-container">
      <div className="journalists-header">
        <h1>{t('journalists.title')}</h1>
        <p>{t('journalists.subtitle')}</p>
      </div>

      <div className="journalists-list">
        {journalists.map(journalist => (
          <div key={journalist.id} className="journalist-card">
            <div className="journalist-header">
              <div className="journalist-avatar">
                <FiUser className="journalist-icon" />
              </div>
              <div className="journalist-info">
                <div className="journalist-name-section">
                  <h3>{journalist.name}</h3>
                  {journalist.verified && (
                    <span className="verified-badge">
                      <FiStar /> {t('journalists.verified')}
                    </span>
                  )}
                </div>
                <div className="journalist-rating">
                  <FiStar className="star-icon" />
                  <span>{journalist.rating}</span>
                </div>
              </div>
            </div>
            
            <p className="journalist-bio">{journalist.bio}</p>
            
            <div className="journalist-details">
              <div className="detail-item">
                <FiMapPin className="detail-icon" />
                <span>{journalist.location}</span>
              </div>
              <div className="detail-item">
                <FiCamera className="detail-icon" />
                <span>{journalist.specialty}</span>
              </div>
              <div className="detail-item">
                <FiAward className="detail-icon" />
                <span>{journalist.experience} {t('journalists.experience')}</span>
              </div>
            </div>
            
            <div className="journalist-skills">
              <div className="skills-section">
                <h4>{t('journalists.languages')}</h4>
                <div className="skills-tags">
                  {journalist.languages.map((language, index) => (
                    <span key={index} className="skill-tag language-tag">
                      {language}
                    </span>
                  ))}
                </div>
              </div>
              
              <div className="skills-section">
                <h4>{t('journalists.equipment')}</h4>
                <div className="skills-tags">
                  {journalist.equipment.map((item, index) => (
                    <span key={index} className="skill-tag equipment-tag">
                      {item}
                    </span>
                  ))}
                </div>
              </div>
            </div>
            
            {journalist.awards.length > 0 && (
              <div className="journalist-awards">
                <h4>{t('journalists.awards')}</h4>
                <div className="awards-list">
                  {journalist.awards.map((award, index) => (
                    <div key={index} className="award-item">
                      <FiAward className="award-icon" />
                      <span>{award}</span>
                    </div>
                  ))}
                </div>
              </div>
            )}
            
            <div className="journalist-footer">
              <span className="media-count">
                {journalist.mediaCount} {t('journalists.mediaItems')}
              </span>
              <button 
                className="view-profile-button"
                onClick={() => handleViewProfile(journalist.id)}
              >
                {t('journalists.viewProfile')}
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Journalists;
