import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../Profile.css';

const CompanyProfile = () => {
  const { id } = useParams();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // TODO: Fetch company profile from API
    setProfile({
      id: 1,
      name: 'Global Media Corp',
      country: 'United States',
      city: 'Los Angeles',
      description: 'Leading media company specializing in international news coverage.',
      requiredServices: ['Photographer', 'Reporter', 'Video Editor'],
      companySize: '201-500',
      industry: 'News',
      rating: 4.2,
      website: 'https://globalmedia.com'
    });
    setLoading(false);
  }, [id]);

  if (loading) {
    return <div className="loading">Loading profile...</div>;
  }

  if (!profile) {
    return <div className="error">Profile not found</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-avatar">
          <img src="/default-company-logo.png" alt={profile.name} />
        </div>
        <div className="profile-info">
          <h1>{profile.name}</h1>
          <p className="location">{profile.city}, {profile.country}</p>
          <p className="industry">{profile.industry}</p>
          <div className="rating">
            <span>â˜… {profile.rating}</span>
          </div>
        </div>
      </div>

      <div className="profile-content">
        <div className="profile-section">
          <h2>About</h2>
          <p>{profile.description}</p>
        </div>

        <div className="profile-section">
          <h2>Company Details</h2>
          <div className="company-details">
            <div className="detail-item">
              <strong>Size:</strong> {profile.companySize} employees
            </div>
            <div className="detail-item">
              <strong>Industry:</strong> {profile.industry}
            </div>
            {profile.website && (
              <div className="detail-item">
                <strong>Website:</strong> <a href={profile.website} target="_blank" rel="noopener noreferrer">{profile.website}</a>
              </div>
            )}
          </div>
        </div>

        <div className="profile-section">
          <h2>Required Services</h2>
          <div className="services-list">
            {profile.requiredServices.map(service => (
              <span key={service} className="service-tag">{service}</span>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CompanyProfile;
