import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { Helmet } from 'react-helmet-async';
import { AuthProvider } from './contexts/AuthContext';
import { LanguageProvider } from './contexts/LanguageContext';
import Header from './components/layout/Header';
import Footer from './components/layout/Footer';
import Home from './pages/Home';
import Login from './pages/auth/Login';
import Register from './pages/auth/Register';
import RegisterJournalist from './pages/auth/RegisterJournalist';
import RegisterCompany from './pages/auth/RegisterCompany';
import Dashboard from './pages/dashboard/Dashboard';
import JournalistProfile from './pages/journalist/JournalistProfile';
import CompanyProfile from './pages/company/CompanyProfile';
import Search from './pages/Search';
import Jobs from './pages/Jobs';
import MediaContent from './pages/MediaContent';
import ProtectedRoute from './components/auth/ProtectedRoute';
import './App.css';

function App() {
  return (
    <LanguageProvider>
      <AuthProvider>
        <div className="App">
          <Helmet>
            <title>WeSourceYou - Global Media Platform</title>
            <meta name="description" content="Connect with journalists and media companies worldwide" />
          </Helmet>
          
          <Header />
          
          <main className="main-content">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="/register/journalist" element={<RegisterJournalist />} />
              <Route path="/register/company" element={<RegisterCompany />} />
              <Route path="/search" element={<Search />} />
              <Route path="/jobs" element={<Jobs />} />
              <Route path="/media" element={<MediaContent />} />
              
              {/* Protected Routes */}
              <Route path="/dashboard" element={
                <ProtectedRoute>
                  <Dashboard />
                </ProtectedRoute>
              } />
              <Route path="/profile/journalist" element={
                <ProtectedRoute>
                  <JournalistProfile />
                </ProtectedRoute>
              } />
              <Route path="/profile/company" element={
                <ProtectedRoute>
                  <CompanyProfile />
                </ProtectedRoute>
              } />
            </Routes>
          </main>
          
          <Footer />
        </div>
      </AuthProvider>
    </LanguageProvider>
  );
}

export default App;
