import React, { useState, useEffect } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import './Jobs.css';

const Jobs = () => {
  const { t } = useLanguage();
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // TODO: Fetch jobs from API
    setJobs([
      {
        id: 1,
        title: 'Senior Photojournalist',
        company: 'Global Media Corp',
        location: 'New York, NY',
        jobType: 'Full-time',
        salary: '$75,000 - $90,000',
        description: 'We are seeking an experienced photojournalist to cover international events and breaking news.',
        postedDate: '2024-01-15'
      },
      {
        id: 2,
        title: 'Video Editor',
        company: 'News Network',
        location: 'Los Angeles, CA',
        jobType: 'Contract',
        salary: '$60,000 - $80,000',
        description: 'Looking for a skilled video editor to join our production team.',
        postedDate: '2024-01-14'
      }
    ]);
    setLoading(false);
  }, []);

  if (loading) {
    return <div className="loading">{t('common.loading')}</div>;
  }

  return (
    <div className="jobs-container">
      <div className="jobs-header">
        <h1>{t('jobs.title')}</h1>
        <p>{t('jobs.subtitle')}</p>
      </div>

      <div className="jobs-list">
        {jobs.map(job => (
          <div key={job.id} className="job-card">
            <div className="job-header">
              <h3>{job.title}</h3>
              <span className="job-type">{job.jobType}</span>
            </div>
            <div className="job-company">{job.company}</div>
            <div className="job-location">{job.location}</div>
            <div className="job-salary">{job.salary}</div>
            <p className="job-description">{job.description}</p>
            <div className="job-footer">
              <span className="job-date">{t('jobs.posted')}: {job.postedDate}</span>
              <button className="apply-button">{t('jobs.applyNow')}</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Jobs;
