import React, { useState, useEffect } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import './MediaContent.css';

const MediaContent = () => {
  const { t } = useLanguage();
  const [mediaContent, setMediaContent] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // TODO: Fetch media content from API
    setMediaContent([
      {
        id: 1,
        title: 'Breaking News Coverage',
        description: 'Exclusive coverage of major international event',
        mediaType: 'Photo',
        price: 500,
        licenseType: 'Rights Managed',
        journalist: 'John Doe',
        rating: 4.5,
        views: 45
      },
      {
        id: 2,
        title: 'Event Photography',
        description: 'Professional event coverage',
        mediaType: 'Photo',
        price: 300,
        licenseType: 'Royalty Free',
        journalist: 'Jane Smith',
        rating: 4.2,
        views: 23
      }
    ]);
    setLoading(false);
  }, []);

  if (loading) {
    return <div className="loading">{t('common.loading')}</div>;
  }

  return (
    <div className="media-content-container">
      <div className="media-content-header">
        <h1>{t('media.title')}</h1>
        <p>{t('media.subtitle')}</p>
      </div>

      <div className="media-content-grid">
        {mediaContent.map(item => (
          <div key={item.id} className="media-card">
            <div className="media-preview">
              <div className="media-placeholder">
                {item.mediaType === 'Photo' ? 'üì∑' : 'üé•'}
              </div>
            </div>
            <div className="media-info">
              <h3>{item.title}</h3>
              <p className="media-description">{item.description}</p>
              <div className="media-meta">
                <span className="media-type">{item.mediaType}</span>
                <span className="media-license">{item.licenseType}</span>
              </div>
              <div className="media-author">{t('media.by')} {item.journalist}</div>
              <div className="media-stats">
                <span>‚òÖ {item.rating}</span>
                <span>üëÅ {item.views} {t('media.views')}</span>
              </div>
              <div className="media-price">${item.price}</div>
              <button className="purchase-button">{t('media.purchase')}</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MediaContent;
