import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import './Auth.css';

const RegisterCompany = () => {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const { register, handleSubmit, formState: { errors }, watch } = useForm();

  const requiredServices = [
    'Producer', 'Reporter', 'TV Cameraman', 'Photographer', 
    'Video Editor', 'Trainer', 'Graphic Designer', 'Lawyer', 
    'Voice-over artist', 'Translator', 'Analyst'
  ];

  const companySizes = ['1-10', '11-50', '51-200', '201-500', '500+'];

  const onSubmit = async (data) => {
    setIsLoading(true);
    try {
      // TODO: Implement registration API call
      console.log('Company registration data:', data);
      navigate('/login');
    } catch (error) {
      console.error('Registration error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h1>Company Registration</h1>
          <p>Create your company profile</p>
        </div>

        <form onSubmit={handleSubmit(onSubmit)} className="auth-form">
          <div className="form-group">
            <label>Company Name *</label>
            <input
              type="text"
              {...register('name', { required: 'Company name is required' })}
              placeholder="Enter your company name"
            />
            {errors.name && <span className="error">{errors.name.message}</span>}
          </div>

          <div className="form-group">
            <label>Contact Person Name *</label>
            <input
              type="text"
              {...register('contactName', { required: 'Contact person name is required' })}
              placeholder="Enter contact person's full name"
            />
            {errors.contactName && <span className="error">{errors.contactName.message}</span>}
          </div>

          <div className="form-group">
            <label>Email *</label>
            <input
              type="email"
              {...register('email', { 
                required: 'Email is required',
                pattern: {
                  value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                  message: 'Invalid email address'
                }
              })}
              placeholder="Enter your email"
            />
            {errors.email && <span className="error">{errors.email.message}</span>}
          </div>

          <div className="form-group">
            <label>Password *</label>
            <input
              type="password"
              {...register('password', { 
                required: 'Password is required',
                minLength: {
                  value: 8,
                  message: 'Password must be at least 8 characters'
                }
              })}
              placeholder="Create a password"
            />
            {errors.password && <span className="error">{errors.password.message}</span>}
          </div>

          <div className="form-group">
            <label>Confirm Password *</label>
            <input
              type="password"
              {...register('confirmPassword', { 
                required: 'Please confirm your password',
                validate: value => value === watch('password') || 'Passwords do not match'
              })}
              placeholder="Confirm your password"
            />
            {errors.confirmPassword && <span className="error">{errors.confirmPassword.message}</span>}
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Country *</label>
              <input
                type="text"
                {...register('country', { required: 'Country is required' })}
                placeholder="Enter your country"
              />
              {errors.country && <span className="error">{errors.country.message}</span>}
            </div>

            <div className="form-group">
              <label>City *</label>
              <input
                type="text"
                {...register('city', { required: 'City is required' })}
                placeholder="Enter your city"
              />
              {errors.city && <span className="error">{errors.city.message}</span>}
            </div>
          </div>

          <div className="form-group">
            <label>Phone Number *</label>
            <input
              type="tel"
              {...register('phoneNumber', { required: 'Phone number is required' })}
              placeholder="Enter your phone number"
            />
            {errors.phoneNumber && <span className="error">{errors.phoneNumber.message}</span>}
          </div>

          <div className="form-group">
            <label>Website (optional)</label>
            <input
              type="url"
              {...register('website')}
              placeholder="https://yourcompany.com"
            />
          </div>

          <div className="form-group">
            <label>Company Size *</label>
            <select {...register('companySize', { required: 'Company size is required' })}>
              <option value="">Select company size</option>
              {companySizes.map(size => (
                <option key={size} value={size}>{size} employees</option>
              ))}
            </select>
            {errors.companySize && <span className="error">{errors.companySize.message}</span>}
          </div>

          <div className="form-group">
            <label>Industry</label>
            <input
              type="text"
              {...register('industry')}
              placeholder="e.g., News, Entertainment, Documentary"
            />
          </div>

          <div className="form-group">
            <label>Company Description *</label>
            <textarea
              {...register('description', { required: 'Company description is required' })}
              placeholder="Tell us about your company and what you do"
              rows="4"
            />
            {errors.description && <span className="error">{errors.description.message}</span>}
          </div>

          <div className="form-group">
            <label>Mission Statement</label>
            <textarea
              {...register('mission')}
              placeholder="Your company's mission statement"
              rows="3"
            />
          </div>

          <div className="form-group">
            <label>Vision Statement</label>
            <textarea
              {...register('vision')}
              placeholder="Your company's vision statement"
              rows="3"
            />
          </div>

          <div className="form-group">
            <label>Required Services *</label>
            <div className="services-selection">
              {requiredServices.map(service => (
                <div key={service} className="checkbox-group">
                  <input
                    type="checkbox"
                    {...register('requiredServices')}
                    value={service}
                    id={service}
                  />
                  <label htmlFor={service}>{service}</label>
                </div>
              ))}
            </div>
            {errors.requiredServices && <span className="error">Please select at least one service</span>}
          </div>

          <div className="form-group">
            <label>Locations (comma-separated)</label>
            <input
              type="text"
              {...register('locations')}
              placeholder="e.g., New York, Los Angeles, London"
            />
          </div>

          <div className="form-group">
            <label>Social Media Accounts</label>
            <div className="social-media-inputs">
              <input
                type="url"
                {...register('socialMedia.facebook')}
                placeholder="Facebook URL"
              />
              <input
                type="url"
                {...register('socialMedia.twitter')}
                placeholder="Twitter URL"
              />
              <input
                type="url"
                {...register('socialMedia.linkedin')}
                placeholder="LinkedIn URL"
              />
              <input
                type="url"
                {...register('socialMedia.other')}
                placeholder="Other social media URL"
              />
            </div>
          </div>

          <div className="form-group">
            <label>Contact Persons (JSON format)</label>
            <textarea
              {...register('contactPersons')}
              placeholder='[{"name": "John Doe", "position": "HR Manager", "email": "john@company.com", "phone": "+1234567890"}]'
              rows="3"
            />
          </div>

          <div className="form-group">
            <label>Payment Methods (comma-separated)</label>
            <input
              type="text"
              {...register('paymentMethods')}
              placeholder="e.g., Credit Card, Bank Transfer, PayPal"
            />
          </div>

          <div className="form-group">
            <label>Preferred Communication</label>
            <select {...register('preferredCommunication')}>
              <option value="">Select preferred communication method</option>
              <option value="Email">Email</option>
              <option value="Phone">Phone</option>
              <option value="Video Call">Video Call</option>
              <option value="In Person">In Person</option>
            </select>
          </div>

          <button type="submit" className="auth-button" disabled={isLoading}>
            {isLoading ? 'Creating Account...' : 'Create Account'}
          </button>
        </form>

        <div className="auth-footer">
          <p>
            Already have an account? <Link to="/login">Sign In</Link>
          </p>
          <p>
            <Link to="/register">← Back to registration options</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default RegisterCompany;
