import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useLanguage } from '../../contexts/LanguageContext';
import '../Profile.css';

const CompanyProfile = () => {
  const { id } = useParams();
  const { t } = useLanguage();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        setLoading(true);
        setError(null);
        
        const response = await fetch(`http://localhost:3001/companies/${id}`);
        
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        
        const data = await response.json();
        setProfile(data);
      } catch (error) {
        console.error('Error fetching company profile:', error);
        setError('Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    if (id) {
      fetchProfile();
    }
  }, [id]);

  if (loading) {
    return <div className="loading">{t('common.loading')}</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!profile) {
    return <div className="error">{t('common.profileNotFound')}</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-avatar">
          <img src={profile.logo || '/default-company-logo.png'} alt={profile.name} />
        </div>
        <div className="profile-info">
          <h1>{profile.name}</h1>
          <p className="location">{profile.user?.city}, {profile.user?.country}</p>
          <p className="industry">{profile.industry}</p>
          <div className="rating">
            <span>★ {profile.rating || 0}</span>
            {profile.isVerified && (
              <span className="verified-badge">✓ {t('companies.verified')}</span>
            )}
          </div>
        </div>
      </div>

      <div className="profile-content">
        <div className="profile-section">
          <h2>{t('companies.about')}</h2>
          <p>{profile.description}</p>
        </div>

        {profile.mission && (
          <div className="profile-section">
            <h2>{t('companies.mission')}</h2>
            <p>{profile.mission}</p>
          </div>
        )}

        {profile.vision && (
          <div className="profile-section">
            <h2>{t('companies.vision')}</h2>
            <p>{profile.vision}</p>
          </div>
        )}

        <div className="profile-section">
          <h2>{t('companies.details')}</h2>
          <div className="company-details">
            <div className="detail-item">
              <strong>{t('companies.size')}:</strong> {profile.companySize}
            </div>
            <div className="detail-item">
              <strong>{t('companies.industry')}:</strong> {profile.industry}
            </div>
            {profile.website && (
              <div className="detail-item">
                <strong>{t('companies.website')}:</strong> <a href={profile.website} target="_blank" rel="noopener noreferrer">{profile.website}</a>
              </div>
            )}
            {profile.locations && (
              <div className="detail-item">
                <strong>{t('companies.locations')}:</strong> {JSON.parse(profile.locations).join(', ')}
              </div>
            )}
            {profile.languages && (
              <div className="detail-item">
                <strong>{t('companies.languages')}:</strong> {JSON.parse(profile.languages).join(', ')}
              </div>
            )}
          </div>
        </div>

        {profile.requiredServices && (
          <div className="profile-section">
            <h2>{t('companies.requiredServices')}</h2>
            <div className="services-list">
              {JSON.parse(profile.requiredServices).map((service, index) => (
                <span key={index} className="service-tag">{service}</span>
              ))}
            </div>
          </div>
        )}

        {profile.specializations && (
          <div className="profile-section">
            <h2>{t('companies.specializations')}</h2>
            <div className="specializations-list">
              {JSON.parse(profile.specializations).map((specialization, index) => (
                <span key={index} className="specialization-tag">{specialization}</span>
              ))}
            </div>
          </div>
        )}

        <div className="profile-section">
          <h2>{t('companies.contact')}</h2>
          <div className="contact-info">
            <p><strong>{t('common.email')}:</strong> {profile.user?.email}</p>
            <p><strong>{t('common.phone')}:</strong> {profile.user?.phone}</p>
            {profile.contactPersons && (
              <div className="contact-persons">
                <strong>{t('companies.contactPersons')}:</strong>
                <ul>
                  {JSON.parse(profile.contactPersons).map((person, index) => (
                    <li key={index}>{person.name} - {person.role}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        </div>

        {profile.jobs && profile.jobs.length > 0 && (
          <div className="profile-section">
            <h2>{t('companies.activeJobs')}</h2>
            <div className="jobs-list">
              {profile.jobs.slice(0, 5).map(job => (
                <div key={job.id} className="job-item">
                  <h4>{job.title}</h4>
                  <p>{job.description}</p>
                  <span className="job-type">{job.jobType}</span>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CompanyProfile;
