import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './Profile.css';

const JournalistProfile = () => {
  const { id } = useParams();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // TODO: Fetch journalist profile from API
    setProfile({
      id: 1,
      name: 'John Doe',
      country: 'United States',
      city: 'New York',
      mediaWorkType: 'Photographer',
      bio: 'Experienced photojournalist with 10+ years covering international events.',
      skills: ['Photojournalism', 'Portrait Photography', 'Event Coverage'],
      languages: ['English', 'Spanish'],
      rating: 4.5,
      hasCamera: true,
      cameraType: 'Canon EOS R5',
      canTravel: true
    });
    setLoading(false);
  }, [id]);

  if (loading) {
    return <div className="loading">Loading profile...</div>;
  }

  if (!profile) {
    return <div className="error">Profile not found</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-avatar">
          <img src="/default-avatar.png" alt={profile.name} />
        </div>
        <div className="profile-info">
          <h1>{profile.name}</h1>
          <p className="location">{profile.city}, {profile.country}</p>
          <p className="profession">{profile.mediaWorkType}</p>
          <div className="rating">
            <span>★ {profile.rating}</span>
          </div>
        </div>
      </div>

      <div className="profile-content">
        <div className="profile-section">
          <h2>About</h2>
          <p>{profile.bio}</p>
        </div>

        <div className="profile-section">
          <h2>Skills</h2>
          <div className="skills-list">
            {profile.skills.map(skill => (
              <span key={skill} className="skill-tag">{skill}</span>
            ))}
          </div>
        </div>

        <div className="profile-section">
          <h2>Languages</h2>
          <div className="languages-list">
            {profile.languages.map(language => (
              <span key={language} className="language-tag">{language}</span>
            ))}
          </div>
        </div>

        <div className="profile-section">
          <h2>Equipment</h2>
          <div className="equipment-info">
            {profile.hasCamera && (
              <div className="equipment-item">
                <strong>Camera:</strong> {profile.cameraType}
              </div>
            )}
            <div className="equipment-item">
              <strong>Travel:</strong> {profile.canTravel ? 'Available' : 'Not available'}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JournalistProfile;
