import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { useLanguage } from '../../contexts/LanguageContext';
import { FiPlus, FiEdit, FiEye, FiBriefcase, FiUsers, FiDollarSign, FiTrendingUp } from 'react-icons/fi';
import './Dashboard.css';

const Dashboard = () => {
  const { user, token } = useAuth();
  const navigate = useNavigate();
  const { t } = useLanguage();
  const [stats, setStats] = useState({
    totalJobs: 0,
    totalApplications: 0,
    totalMediaContent: 0,
    totalRevenue: 0
  });
  const [companyJobs, setCompanyJobs] = useState([]);
  const [companyApplications, setCompanyApplications] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user?.role === 'company') {
      fetchCompanyData();
    } else {
      // For other roles, use mock data for now
      setStats({
        totalJobs: 25,
        totalApplications: 12,
        totalMediaContent: 8,
        totalRevenue: 2500
      });
      setLoading(false);
    }
  }, [user]);

  const fetchCompanyData = async () => {
    try {
      setLoading(true);
      
      // Fetch company jobs
      const jobsResponse = await fetch('http://localhost:3001/companies/my/jobs', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (jobsResponse.ok) {
        const jobs = await jobsResponse.json();
        setCompanyJobs(jobs);
        setStats(prev => ({ ...prev, totalJobs: jobs.length }));
      }

      // Fetch company applications
      const applicationsResponse = await fetch('http://localhost:3001/companies/my/applications', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      
      if (applicationsResponse.ok) {
        const applications = await applicationsResponse.json();
        setCompanyApplications(applications);
        setStats(prev => ({ ...prev, totalApplications: applications.length }));
      }

    } catch (error) {
      console.error('Error fetching company data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAddJob = () => {
    navigate('/jobs/create');
  };

  const handleViewProfile = () => {
    navigate('/profile/company');
  };

  const handleViewJobs = () => {
    navigate('/jobs/my');
  };

  const handleViewApplications = () => {
    navigate('/applications');
  };

  const renderJournalistDashboard = () => (
    <div className="dashboard-content">
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Jobs Applied</h3>
          <p className="stat-number">{stats.totalApplications}</p>
        </div>
        <div className="stat-card">
          <h3>Media Content</h3>
          <p className="stat-number">{stats.totalMediaContent}</p>
        </div>
        <div className="stat-card">
          <h3>Total Revenue</h3>
          <p className="stat-number">${stats.totalRevenue}</p>
        </div>
        <div className="stat-card">
          <h3>Profile Views</h3>
          <p className="stat-number">156</p>
        </div>
      </div>

      <div className="dashboard-sections">
        <div className="section">
          <h2>Recent Job Applications</h2>
          <div className="job-applications">
            <div className="job-application">
              <h4>Senior Photojournalist</h4>
              <p>Global Media Corp</p>
              <span className="status pending">Pending</span>
            </div>
            <div className="job-application">
              <h4>Video Editor</h4>
              <p>News Network</p>
              <span className="status under-review">Under Review</span>
            </div>
          </div>
        </div>

        <div className="section">
          <h2>Your Media Content</h2>
          <div className="media-content">
            <div className="media-item">
              <h4>Breaking News Coverage</h4>
              <p>Status: Published</p>
              <p>Views: 45</p>
            </div>
            <div className="media-item">
              <h4>Event Photography</h4>
              <p>Status: Draft</p>
              <p>Views: 0</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const renderCompanyDashboard = () => (
    <div className="dashboard-content">
      <div className="dashboard-actions">
        <button className="action-button primary" onClick={handleAddJob}>
          <FiPlus />
          {t('dashboard.addJob')}
        </button>
        <button className="action-button secondary" onClick={handleViewProfile}>
          <FiEdit />
          {t('dashboard.editProfile')}
        </button>
        <button className="action-button secondary" onClick={handleViewJobs}>
          <FiBriefcase />
          {t('dashboard.viewJobs')}
        </button>
        <button className="action-button secondary" onClick={handleViewApplications}>
          <FiUsers />
          {t('dashboard.viewApplications')}
        </button>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <h3>{t('dashboard.activeJobs')}</h3>
          <p className="stat-number">{stats.totalJobs}</p>
          <FiBriefcase className="stat-icon" />
        </div>
        <div className="stat-card">
          <h3>{t('dashboard.applications')}</h3>
          <p className="stat-number">{stats.totalApplications}</p>
          <FiUsers className="stat-icon" />
        </div>
        <div className="stat-card">
          <h3>{t('dashboard.mediaPurchases')}</h3>
          <p className="stat-number">8</p>
          <FiTrendingUp className="stat-icon" />
        </div>
        <div className="stat-card">
          <h3>{t('dashboard.profileViews')}</h3>
          <p className="stat-number">89</p>
          <FiEye className="stat-icon" />
        </div>
      </div>

      <div className="dashboard-sections">
        <div className="section">
          <div className="section-header">
            <h2>{t('dashboard.recentApplications')}</h2>
            <button className="view-all-btn" onClick={handleViewApplications}>
              {t('dashboard.viewAll')}
            </button>
          </div>
          <div className="job-applications">
            {loading ? (
              <div className="loading">Loading applications...</div>
            ) : companyApplications.length > 0 ? (
              companyApplications.slice(0, 5).map((application) => (
                <div key={application.id} className="job-application">
                  <div className="application-info">
                    <h4>{application.job?.title || 'Job Title'}</h4>
                    <p>{application.journalist?.user?.firstName} {application.journalist?.user?.lastName}</p>
                    <span className="status pending">{application.status || 'Pending Review'}</span>
                  </div>
                  <div className="application-actions">
                    <button className="action-btn small">
                      <FiEye />
                      {t('dashboard.view')}
                    </button>
                  </div>
                </div>
              ))
            ) : (
              <div className="empty-state">
                <p>{t('dashboard.noApplications')}</p>
              </div>
            )}
          </div>
        </div>

        <div className="section">
          <div className="section-header">
            <h2>{t('dashboard.activeJobs')}</h2>
            <button className="view-all-btn" onClick={handleViewJobs}>
              {t('dashboard.viewAll')}
            </button>
          </div>
          <div className="jobs-list">
            {loading ? (
              <div className="loading">Loading jobs...</div>
            ) : companyJobs.length > 0 ? (
              companyJobs.slice(0, 5).map((job) => (
                <div key={job.id} className="job-item">
                  <div className="job-info">
                    <h4>{job.title}</h4>
                    <p>{job.description?.substring(0, 100)}...</p>
                    <span className="job-type">{job.jobType}</span>
                    <span className="applications-count">
                      {job.applications?.length || 0} {t('dashboard.applications')}
                    </span>
                  </div>
                  <div className="job-actions">
                    <button className="action-btn small">
                      <FiEdit />
                      {t('dashboard.edit')}
                    </button>
                    <button className="action-btn small">
                      <FiEye />
                      {t('dashboard.view')}
                    </button>
                  </div>
                </div>
              ))
            ) : (
              <div className="empty-state">
                <p>{t('dashboard.noJobs')}</p>
                <button className="action-button primary" onClick={handleAddJob}>
                  <FiPlus />
                  {t('dashboard.addFirstJob')}
                </button>
              </div>
            )}
          </div>
        </div>

        <div className="section">
          <h2>{t('dashboard.recentMediaPurchases')}</h2>
          <div className="media-content">
            <div className="media-item">
              <h4>Breaking News Coverage</h4>
              <p>{t('dashboard.price')}: $500</p>
              <p>{t('dashboard.status')}: {t('dashboard.completed')}</p>
            </div>
            <div className="media-item">
              <h4>Event Photography</h4>
              <p>{t('dashboard.price')}: $300</p>
              <p>{t('dashboard.status')}: {t('dashboard.pending')}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const renderAdminDashboard = () => (
    <div className="dashboard-content">
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Users</h3>
          <p className="stat-number">1,234</p>
        </div>
        <div className="stat-card">
          <h3>Total Jobs</h3>
          <p className="stat-number">567</p>
        </div>
        <div className="stat-card">
          <h3>Total Media</h3>
          <p className="stat-number">890</p>
        </div>
        <div className="stat-card">
          <h3>Revenue</h3>
          <p className="stat-number">$12,345</p>
        </div>
      </div>

      <div className="dashboard-sections">
        <div className="section">
          <h2>System Overview</h2>
          <p>Admin dashboard content will be implemented here.</p>
        </div>
      </div>
    </div>
  );

  if (loading) {
    return <div className="loading">{t('common.loading')}</div>;
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>{t('dashboard.welcome')}, {user?.firstName || 'User'}!</h1>
        <p>{t('dashboard.subtitle')}</p>
      </div>

      {user?.role === 'Journalist' && renderJournalistDashboard()}
      {user?.role === 'Company' && renderCompanyDashboard()}
      {user?.role === 'Admin' && renderAdminDashboard()}
    </div>
  );
};

export default Dashboard;
