import React, { useState } from 'react';
import './Search.css';
import searchAPI from '../api/searchAPI';

const Search = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchType, setSearchType] = useState('all');
  const [results, setResults] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [filters, setFilters] = useState({});

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchTerm.trim()) return;

    setIsLoading(true);
    try {
      const searchParams = {
        query: searchTerm,
        type: searchType,
        ...filters
      };

      const response = await searchAPI.global(searchTerm, searchParams);
      setResults(response.data || {});
    } catch (error) {
      console.error('Search error:', error);
      // Show empty results on error
      setResults({});
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="search-container">
      <div className="search-header">
        <h1>Search WeSourceYou</h1>
        <p>Find journalists, companies, jobs, and media content</p>
      </div>

      <form onSubmit={handleSearch} className="search-form">
        <div className="search-input-group">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Search for journalists, companies, jobs, or media content..."
            className="search-input"
          />
          <select
            value={searchType}
            onChange={(e) => setSearchType(e.target.value)}
            className="search-type-select"
          >
            <option value="all">All</option>
            <option value="journalists">Journalists</option>
            <option value="companies">Companies</option>
            <option value="jobs">Jobs</option>
            <option value="media">Media Content</option>
          </select>
          <button type="submit" className="search-button">
            Search
          </button>
        </div>
      </form>

      <div className="search-results">
        {isLoading ? (
          <p>Loading results...</p>
        ) : results && Object.keys(results).length > 0 ? (
          <div>
            <h2>Search Results</h2>
            <p>Found {Object.keys(results).length} results for "{searchTerm}"</p>
            {Object.entries(results).map(([key, value]) => (
              <div key={key}>
                <h3>{key}</h3>
                <p>{JSON.stringify(value)}</p>
              </div>
            ))}
          </div>
        ) : (
          <p>No results found for "{searchTerm}". Try a different search term or type.</p>
        )}
      </div>
    </div>
  );
};

export default Search;
