import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useLanguage } from '../../contexts/LanguageContext';
import { FiEdit, FiSave, FiX, FiMapPin, FiMail, FiPhone, FiGlobe, FiStar } from 'react-icons/fi';
import MultiSelectField from '../../components/admin/MultiSelectField';
import '../Profile.css';

const JournalistProfile = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user, token } = useAuth();
  const { t } = useLanguage();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editForm, setEditForm] = useState({});

  // Helper function to safely parse JSON arrays
  const parseArrayField = (field) => {
    if (!field) return [];
    if (Array.isArray(field)) return field;
    try {
      return JSON.parse(field);
    } catch (error) {
      console.warn('Failed to parse field as JSON:', field);
      return [];
    }
  };

  const isOwnProfile = !id || (user?.role === 'journalist' && user?.id === profile?.user?.id);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        setLoading(true);
        setError(null);
        
        const endpoint = isOwnProfile 
          ? 'http://localhost:3001/journalists/my/profile'
          : `http://localhost:3001/journalists/${id}`;
        
        const headers = {
          'Content-Type': 'application/json'
        };
        
        if (isOwnProfile && token) {
          headers['Authorization'] = `Bearer ${token}`;
        }
        
        const response = await fetch(endpoint, { headers });
        
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        
        const data = await response.json();
        setProfile(data);
        setEditForm(data);
      } catch (error) {
        console.error('Error fetching journalist profile:', error);
        setError('Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [id, isOwnProfile, token]);

  const handleEdit = () => {
    setIsEditing(true);
    setEditForm(profile);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setEditForm(profile);
  };

  const handleSave = async () => {
    try {
      const response = await fetch('http://localhost:3001/journalists/my/profile', {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(editForm)
      });

      if (response.ok) {
        const updatedProfile = await response.json();
        setProfile(updatedProfile);
        setIsEditing(false);
      } else {
        throw new Error('Failed to update profile');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleArrayChange = (fieldName, value) => {
    setEditForm(prev => ({
      ...prev,
      [fieldName]: value
    }));
  };

  if (loading) {
    return <div className="loading">{t('common.loading')}</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!profile) {
    return <div className="error">{t('common.profileNotFound')}</div>;
  }

  // Parse arrays safely
  const skills = parseArrayField(profile.skills);
  const languages = parseArrayField(profile.languages);
  const certifications = parseArrayField(profile.certifications);

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="profile-avatar">
          <img src={profile.user?.profilePicture || '/default-avatar.png'} alt="Profile" />
        </div>
        <div className="profile-info">
          <div className="profile-title-section">
            <h1>{profile.user?.firstName} {profile.user?.lastName}</h1>
            {isOwnProfile && (
              <div className="profile-actions">
                {!isEditing ? (
                  <button className="action-btn primary" onClick={handleEdit}>
                    <FiEdit />
                    {t('common.edit')}
                  </button>
                ) : (
                  <>
                    <button className="action-btn success" onClick={handleSave}>
                      <FiSave />
                      {t('common.save')}
                    </button>
                    <button className="action-btn secondary" onClick={handleCancel}>
                      <FiX />
                      {t('common.cancel')}
                    </button>
                  </>
                )}
              </div>
            )}
          </div>
          <p className="profile-location">
            <FiMapPin />
            {profile.user?.city}, {profile.user?.country}
          </p>
          <p className="profile-specialty">{profile.mediaWorkType}</p>
          <div className="profile-rating">
            <FiStar />
            <span>{profile.rating || 0}</span>
            {profile.isVerified && (
              <span className="verified-badge">âœ“ {t('journalists.verified')}</span>
            )}
          </div>
        </div>
      </div>

      <div className="profile-content">
        <div className="profile-section">
          <h2>{t('journalists.bio')}</h2>
          {isEditing ? (
            <textarea
              name="bio"
              value={editForm.bio || ''}
              onChange={handleInputChange}
              placeholder={t('journalists.bioPlaceholder')}
              className="edit-textarea"
            />
          ) : (
            <p>{profile.bio}</p>
          )}
        </div>

        <div className="profile-section">
          <h2>{t('journalists.skills')}</h2>
          {isEditing ? (
            <MultiSelectField
              label=""
              value={Array.isArray(editForm.skills) ? editForm.skills : []}
              onChange={(skills) => handleArrayChange('skills', skills)}
              options={[
                'Video Production',
                'Photo Journalism',
                'Conflict Reporting',
                'Political Analysis',
                'Live Broadcasting',
                'Video Editing',
                'Audio Production',
                'Interviewing',
                'Research',
                'Translation',
                'Graphic Design',
                'Social Media Management',
                'Drone Operation',
                'First Aid',
                'Satellite Communication'
              ]}
              placeholder="Type to add custom skill..."
              maxItems={15}
            />
          ) : (
            <div className="skills-grid">
              {skills.map((skill, index) => (
                <span key={index} className="skill-tag">{skill}</span>
              ))}
            </div>
          )}
        </div>

        <div className="profile-section">
          <h2>{t('journalists.languages')}</h2>
          {isEditing ? (
            <MultiSelectField
              label=""
              value={Array.isArray(editForm.languages) ? editForm.languages : []}
              onChange={(languages) => handleArrayChange('languages', languages)}
              options={[
                'English',
                'Arabic',
                'French',
                'Spanish',
                'German',
                'Italian',
                'Portuguese',
                'Russian',
                'Chinese',
                'Japanese',
                'Korean',
                'Turkish',
                'Persian',
                'Kurdish',
                'Hebrew',
                'Hindi',
                'Urdu',
                'Bengali',
                'Thai',
                'Vietnamese'
              ]}
              placeholder="Type to add custom language..."
              maxItems={10}
            />
          ) : (
            <div className="languages-grid">
              {languages.map((language, index) => (
                <span key={index} className="language-tag">{language}</span>
              ))}
            </div>
          )}
        </div>

        <div className="profile-section">
          <h2>{t('journalists.certifications')}</h2>
          {isEditing ? (
            <MultiSelectField
              label=""
              value={Array.isArray(editForm.certifications) ? editForm.certifications : []}
              onChange={(certifications) => handleArrayChange('certifications', certifications)}
              options={[
                'War Correspondent Certification',
                'First Aid Training',
                'Hostile Environment Training',
                'Satellite Communication Certification',
                'Drone Operation License',
                'Press Accreditation',
                'Media Ethics Certification',
                'Digital Security Training',
                'Conflict Resolution Training',
                'Cultural Sensitivity Training',
                'Emergency Response Certification',
                'Broadcast Journalism Certificate',
                'Photojournalism Certification',
                'Video Production Certification',
                'Audio Engineering Certificate'
              ]}
              placeholder="Type to add custom certification..."
              maxItems={10}
            />
          ) : (
            <div className="certifications-grid">
              {certifications.map((certification, index) => (
                <span key={index} className="certification-tag">{certification}</span>
              ))}
            </div>
          )}
        </div>

        <div className="profile-section">
          <h2>{t('journalists.details')}</h2>
          <div className="journalist-details">
            <div className="detail-item">
              <strong>{t('journalists.mediaWorkType')}:</strong>
              {isEditing ? (
                <select
                  name="mediaWorkType"
                  value={editForm.mediaWorkType || ''}
                  onChange={handleInputChange}
                  className="edit-select"
                >
                  <option value="">Select media work type</option>
                  <option value="Video Production">Video Production</option>
                  <option value="Photo Journalism">Photo Journalism</option>
                  <option value="Conflict Reporting">Conflict Reporting</option>
                  <option value="Political Analysis">Political Analysis</option>
                  <option value="Live Broadcasting">Live Broadcasting</option>
                </select>
              ) : (
                profile.mediaWorkType
              )}
            </div>
            <div className="detail-item">
              <strong>{t('journalists.analystSpecialty')}:</strong>
              {isEditing ? (
                <select
                  name="analystSpecialty"
                  value={editForm.analystSpecialty || ''}
                  onChange={handleInputChange}
                  className="edit-select"
                >
                  <option value="">Select analyst specialty</option>
                  <option value="Political Analysis">Political Analysis</option>
                  <option value="Economic Analysis">Economic Analysis</option>
                  <option value="Social Analysis">Social Analysis</option>
                  <option value="Security Analysis">Security Analysis</option>
                  <option value="Cultural Analysis">Cultural Analysis</option>
                </select>
              ) : (
                profile.analystSpecialty
              )}
            </div>
            <div className="detail-item">
              <strong>{t('journalists.experienceLevel')}:</strong>
              {isEditing ? (
                <select
                  name="experienceLevel"
                  value={editForm.experienceLevel || ''}
                  onChange={handleInputChange}
                  className="edit-select"
                >
                  <option value="">Select experience level</option>
                  <option value="Entry Level">Entry Level</option>
                  <option value="Mid Level">Mid Level</option>
                  <option value="Senior Level">Senior Level</option>
                  <option value="Expert Level">Expert Level</option>
                </select>
              ) : (
                profile.experienceLevel
              )}
            </div>
            <div className="detail-item">
              <strong>{t('journalists.yearsOfExperience')}:</strong> {profile.yearsOfExperience || 0}
            </div>
            <div className="detail-item">
              <strong>{t('journalists.completedProjects')}:</strong> {profile.completedProjects || 0}
            </div>
          </div>
        </div>

        <div className="profile-section">
          <h2>{t('journalists.contact')}</h2>
          <div className="contact-info">
            <p><FiMail /> <strong>{t('common.email')}:</strong> {profile.user?.email}</p>
            <p><FiPhone /> <strong>{t('common.phone')}:</strong> {profile.user?.phoneNumber}</p>
            {profile.website && (
              <p><FiGlobe /> <strong>{t('journalists.website')}:</strong> 
                <a href={profile.website} target="_blank" rel="noopener noreferrer">
                  {profile.website}
                </a>
              </p>
            )}
          </div>
        </div>

        {profile.jobApplications && profile.jobApplications.length > 0 && (
          <div className="profile-section">
            <h2>{t('journalists.recentApplications')}</h2>
            <div className="applications-list">
              {profile.jobApplications.slice(0, 5).map(application => (
                <div key={application.id} className="application-item">
                  <h4>{application.job?.title}</h4>
                  <p>{application.job?.company?.name}</p>
                  <span className="status">{application.status}</span>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default JournalistProfile;
